package com.icbcaxa.eata.token;import lombok.Data;import org.springframework.boot.context.properties.ConfigurationProperties;import org.springframework.boot.context.properties.EnableConfigurationProperties;import org.springframework.context.annotation.PropertySource;import org.springframework.stereotype.Component;@Data@Component@EnableConfigurationProperties@ConfigurationProperties(prefix = "fulan.token.jwt")@PropertySource(value = { "classpath:/security-jwt.properties" }, ignoreResourceNotFound = true)public class TokenConfig {		private  String issuer;    private  String seed;    private  int TTL;      private  int refreshSpan;      private  int refreshTTL;	public String getIssuer() {		return issuer;	}	public void setIssuer(String issuer) {		this.issuer = issuer;	}	public String getSeed() {		return seed;	}	public void setSeed(String seed) {		this.seed = seed;	}	public int getTTL() {		return TTL;	}	public void setTTL(int tTL) {		TTL = tTL;	}	public int getRefreshSpan() {		return refreshSpan;	}	public void setRefreshSpan(int refreshSpan) {		this.refreshSpan = refreshSpan;	}	public int getRefreshTTL() {		return refreshTTL;	}	public void setRefreshTTL(int refreshTTL) {		this.refreshTTL = refreshTTL;	}          }